INSERT INTO `testcase` (`caseId`, `name`, `description`, `messageType`, `messageName`, `testType`, `executeType`, `initiatorInd`, `responderInd`, `ssnHandlingInd`, `expectedTestResults`, `createtime`, `createuser`, `changedtime`, `changeduser`, `nhinSpecHtml`) VALUES
	(1, 'PDI-1.1c', 'Participant transmits to the Testing Tool a synchronous patient discovery request with Patient Name, Gender and Date of Birth as described. (The test case verifies only the requested parameters, although the participant system can send additional attributes, depending on system function.)', 'PD', 'Patient Discovery', 'I', 'R', 'Y', 'N', 'N', 'The Testing Tool returns a patient discovery response with a match.  Participant is able to correlate the patient.', NULL, 'user', NULL, 'user', '<p><strong>Patient Discovery Specification version 1.0</strong></p><p><strong>3.1.4</strong> Demographic requirements in request and response</p><p>Gateways should specify an appropriate collection of demographics that balance the needs of:<br>a) Reliable demographic matching to ensure a minimum of false negative and false positive matches<br>b) Avoidance of privacy issues in case of a security break especially to avoid potential for identity fraud as a result of a security break</p><p>Required values for the Patient Discovery request:</p><ul><li>LivingSubjectName Parameter: Both "family" and "given" elements are required.  If patients are known by multiple names or have had a name change, the alternative names shall be specified as multiple instances of LivingSubjectName.  Inclusion of all current and former names increases the likelihood of a correct match but may incur privacy concerns.  The sequence of given names in the given name list reflects the sequence in which they are known â€“ first, second, third etc. The first name is specified in the first "given" element in the list.  Any middle name is specified in the second "given" element in the list when there are no more than two "given" elements.</li><li>LivingSubjectAdministrativeGender Parameter: Is required.  Coded using the HL7 coding for AdministrativeGender; namely code equal to one of "M" (Male) or "F "(Female) or "UN" (Undifferentiated).</li><li>LivingSubjectBirthTime Parameter: Is required.  The contents must contain the greatest degree of detail as is available.</li></ul><p>Required values for the Patient Discovery response (unless the Shared/national Patient Identifier Query and Feed mode is used):</p><ul><li>Person.name element: Both "family" and "given" elements are required.  If patients are known by multiple names or have had a name change, the alternative names shall be specified as multiple  Patient.name elements.  Inclusion of all current and former names increases the likelihood of a correct match.  The sequence of given names in the given name list reflects the sequence in which they are known â€“ first, second, third etc. The first name is specified in the first "given" element in the list.  Any middle name is specified in the second "given" element in the list when there are no more than two "given" elements.</li><li>Person.administrativeGenderCode element: Is required.  Coded using the HL7 coding for AdministrativeGender; namely code equal to one of "M" (Male) or "F "(Female) or "UN" (Undifferentiated).</li><li>Person.birthTime Parameter: Is required.  The contents must contain the greatest degree of detail as is available.</li></ul>'),
	(2, 'PDI-1.2', 'Participant transmits to the Testing Tool a synchronous patient discovery request with Patient Name, Gender, Date of Birth, a single address and a single phone number as described.  (The test case verifies only the requested parameters, although the participant system can send additional attributes, depending on system function.)', 'PD', 'Patient Discovery', 'I', 'R', 'Y', 'N', 'N', 'The Testing Tool returns a patient discovery response with a match.  Participant is able to correlate the patient.', NULL, 'user', NULL, 'user', '<p><strong>Patient Discovery Specification version 1.0</strong></p><p><strong>3.1.4</strong> Demographic requirements in request and response</p><p>Gateways should specify an appropriate collection of demographics that balance the needs of:<br>a) Reliable demographic matching to ensure a minimum of false negative and false positive matches<br>b) Avoidance of privacy issues in case of a security break especially to avoid potential for identity fraud as a result of a security break</p><p>Required values for the Patient Discovery request:</p><ul><li>LivingSubjectName Parameter: Both "family" and "given" elements are required.  If patients are known by multiple names or have had a name change, the alternative names shall be specified as multiple instances of LivingSubjectName.  Inclusion of all current and former names increases the likelihood of a correct match but may incur privacy concerns.  The sequence of given names in the given name list reflects the sequence in which they are known â€“ first, second, third etc. The first name is specified in the first "given" element in the list.  Any middle name is specified in the second "given" element in the list when there are no more than two "given" elements.</li><li>LivingSubjectAdministrativeGender Parameter: Is required.  Coded using the HL7 coding for AdministrativeGender; namely code equal to one of "M" (Male) or "F "(Female) or "UN" (Undifferentiated).</li><li>LivingSubjectBirthTime Parameter: Is required.  The contents must contain the greatest degree of detail as is available.</li></ul><p>Required values for the Patient Discovery response (unless the Shared/national Patient Identifier Query and Feed mode is used):</p><ul><li>Person.name element: Both "family" and "given" elements are required.  If patients are known by multiple names or have had a name change, the alternative names shall be specified as multiple  Patient.name elements.  Inclusion of all current and former names increases the likelihood of a correct match.  The sequence of given names in the given name list reflects the sequence in which they are known â€“ first, second, third etc. The first name is specified in the first "given" element in the list.  Any middle name is specified in the second "given" element in the list when there are no more than two "given" elements.</li><li>Person.administrativeGenderCode element: Is required.  Coded using the HL7 coding for AdministrativeGender; namely code equal to one of "M" (Male) or "F "(Female) or "UN" (Undifferentiated).</li><li>Person.birthTime Parameter: Is required.  The contents must contain the greatest degree of detail as is available.</li></ul>'),
	(3, 'PDI-1.3', 'Participant transmits to the Testing Tool a synchronous patient discovery request with Patient Name, Gender,  Date of Birth, multiple addresses and multiple phone numbers as described.  (The test case verifies only the requested parameters, although the participant system can send additional attributes, depending on system function.)', 'PD', 'Patient Discovery', 'I', 'O', 'Y', 'N', 'N', 'The Testing Tool returns a patient discovery response with a match.  Participant is able to correlate the patient.', NULL, 'user', NULL, 'user', '<p><strong>Patient Discovery Specification version 1.0</strong></p><p><strong>3.1.4</strong> Demographic requirements in request and response</p><p>Gateways should specify an appropriate collection of demographics that balance the needs of:<br>a) Reliable demographic matching to ensure a minimum of false negative and false positive matches<br>b) Avoidance of privacy issues in case of a security break especially to avoid potential for identity fraud as a result of a security break</p><p>Required values for the Patient Discovery request:</p><ul><li>LivingSubjectName Parameter: Both "family" and "given" elements are required.  If patients are known by multiple names or have had a name change, the alternative names shall be specified as multiple instances of LivingSubjectName.  Inclusion of all current and former names increases the likelihood of a correct match but may incur privacy concerns.  The sequence of given names in the given name list reflects the sequence in which they are known â€“ first, second, third etc. The first name is specified in the first "given" element in the list.  Any middle name is specified in the second "given" element in the list when there are no more than two "given" elements.</li><li>LivingSubjectAdministrativeGender Parameter: Is required.  Coded using the HL7 coding for AdministrativeGender; namely code equal to one of "M" (Male) or "F "(Female) or "UN" (Undifferentiated).</li><li>LivingSubjec